INSERT INTO `page` (`id`,`route`,`title`,`body`,`last_edit_dt`,`user_id`,`slug`) VALUES (4,'/sibley/staff','Sibley Iowa','<div class=\"container\">\r\n<div class=\"row\">\r\n<div class=\"col-md-6\">\r\n<h3>City of Sibley: Mission</h3>\r\n\r\n<p>Increase opportunities that will enhance the quality of life for all ages.</p>\r\n\r\n<h3>Vision Statement</h3>\r\n\r\n<p>Sibley, located in beautiful NW Iowa offers an energetic community, large retail base, affordable housing, and modern telecommunications. We Strive to provide an excellent quality of life for all through a great medical community, a diverse and thriving industrial base, great educational and recreational opportunities. Sibley, Iowa, unlimited opportunities with strong family values.</p>\r\n</div>\r\n\r\n<div class=\"col-md-6\"><img alt=\"\" class=\"d-md-block d-none featured-img img-fluid rounded-circle\" src=\"/img/cityOffice.jpg\" /></div>\r\n</div>\r\n\r\n<div class=\"row\">\r\n<div class=\"col-md-12\">\r\n<h3>About Sibley</h3>\r\n\r\n<p>Sibley, located in beautiful NW Iowa offers an energetic community, large retail base, affordable housing, and modern telecommunications. We Strive to provide an excellent quality of life for all through a great medical community, a diverse and thriving industrial base, great educational and recreational opportunities. Sibley, Iowa, unlimited opportunities with strong family values.</p>\r\n</div>\r\n</div>\r\n</div>\r\n','2019-01-02 20:31:38',1,'sibley-iowa');
INSERT INTO `page` (`id`,`route`,`title`,`body`,`last_edit_dt`,`user_id`,`slug`) VALUES (5,'/sibley/lodging','Lodging in or near Sibley','<p>In addition to two hotels, Sibley has a beautiful campground in&nbsp;Sam Robinson Memorial Park. Several other campgrounds are&nbsp;nearby. Those include the Hawkeye Point campground and Willow Creek.</p>\r\n','2019-01-03 17:49:18',1,'lodging-in-or-near-sibley');
