INSERT INTO `page` (`id`,`route`,`title`,`body`,`last_edit_dt`,`user_id`,`slug`) VALUES (7,'/sibley/chamber','Sibley Chamber of Commerce','<div class=\"row\">\r\n<div class=\"col-md-7\">\r\n<blockquote class=\"blockquote\">\r\n<p class=\"mb-0\">To Provide leadership to enhance the quality of life of the community through the unified efforts of its members, volunteers, businesses, and citizens</p>\r\n\r\n<footer class=\"blockquote-footer\">Mission</footer>\r\n</blockquote>\r\n\r\n<p>Sibley Chamber of Commerce<br>\r\n808 3rd Avenue<br>\r\nPO Box 126<br>\r\nSibley, IA 51249</p>\r\n</div>\r\n\r\n<div class=\"col-md-5\"><img alt=\"Sibley Chamber\" class=\"d-md-block d-none featured-img img-fluid rounded-circle\" src=\"/img/assets/1547315146165.jpg\" /></div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hours:</strong></p>\r\n\r\n<ul>\r\n	<li>Monday - Friday: 8:00 a.m. - 5:00 p.m.</li>\r\n</ul>\r\n\r\n<p><strong>Office Personnel</strong></p>\r\n\r\n<ul>\r\n	<li>Ashley Goettig - Executive Director</li>\r\n</ul>\r\n\r\n<p><strong>Current Board Members</strong></p>\r\n\r\n<ul>\r\n	<li>Pastor Barb Kopperud&nbsp;- Ministerial Representative</li>\r\n	<li>Jan Henningsen - City Council Representative</li>\r\n	<li>Jenn Greve Business&nbsp;Representative</li>\r\n	<li>Travis Ten Napel&nbsp;- Business Representative</li>\r\n	<li>Brandon Rusche&nbsp;- Business Representative</li>\r\n	<li>Dan Janssen</li>\r\n</ul>\r\n','2019-01-12 12:01:00',1,'sibley-chamber-of-commerce');
INSERT INTO `page` (`id`,`route`,`title`,`body`,`last_edit_dt`,`user_id`,`slug`) VALUES (8,'/sibley/chamber-benefits','Sibley Chamber of Commerce Membership Benefits','<h3>Chamber Member Benefits</h3>\r\n\r\n<p><strong>Friday Forecast</strong> - Weekly notice of upcoming events. &nbsp;Advertising Opportunities are available as inserts or box ads, website, and newspaper promotional advertisements.</p>\r\n\r\n<p><strong>1st Dollar a Chamber Open House</strong> - Every new business in Sibley is offered a &quot;First Dollar&quot; which extends an invitation to all Chamber businesses to come and welcome them to the community and includes a photo in the Gazette Tribune. Also, all Chamber members are encouraged to sign up for a &quot;Chamber Open House&quot; to celebrate a Grand Opening, remodeling, or relocation. This also includes a photo in the Gazette Tribune.</p>\r\n\r\n<p><strong>Promotional literature Distribution</strong> - Members can display brochures or other materials in the Chamber office at no cost, giving your business exposure to thousands of residents and visitors who browse through our information center year-round.</p>\r\n\r\n<p><strong>Welcome to Sibley Newcomer and Visitor packet</strong> - A goodie bag packed with information on our community and Chamber businesses made available to visitors and newcomers to Sibley. Excellent opportunity to put your business at the fingertips of your newest customers.</p>\r\n\r\n<p><strong>Special Events</strong> - Offer several opportunities to be a business sponsor, having your business name out in the community.</p>\r\n\r\n<p><strong>Committee &amp; Task Force Meetings</strong> - Participation in committee activities and working with others on major projects and events offers members additional opportunities to meet business &amp; professional people with similar interests. Not to mention an excellent opportunity for your employees to be a representative for your business.<br />\r\nReferrals</p>\r\n\r\n<p>The Chamber receives hundreds of requests each year for recommendations on goods and services to be purchased in Sibley. We refer ONLY Chamber members to newcomers, visitors and residents.<br />\r\n&nbsp;</p>\r\n\r\n<h3>Member Service</h3>\r\n\r\n<p><strong>Membership Directory</strong> - Alphabetical list of all Chamber members . This is NOT available to non-members.</p>\r\n\r\n<p><strong>Mailing Labels</strong> - Self adhesive labels complete with name, address, and phone numbers of all members, area businesses and community leaders. (small additional fee)</p>\r\n\r\n<p><strong>Assistance with Problems</strong> - At the Chamber, we may not have every answer, but if we don&#39;t, we know who does. We will do our best to assist you in solving any business related problem you may have.</p>\r\n','2019-01-12 12:06:11',1,'sibley-chamber-of-commerce-membership-benefits');
